// Get datasets by type
inline std::vector<DatasetInfo> get_stepsize_datasets() {
    auto all = get_all_datasets();
    std::vector<DatasetInfo> result;
    for (const auto& ds : all) {
        if (ds.name.find("stepsize") != std::string::npos) {
            result.push_back(ds);
        }
    }
    return result;
}

inline std::vector<DatasetInfo> get_timelength_datasets() {
    auto all = get_all_datasets();
    std::vector<DatasetInfo> result;
    for (const auto& ds : all) {
        if (ds.name.find("timelength") != std::string::npos) {
            result.push_back(ds);
        }
    }
    return result;
}

